"use client"

import { cn } from "@/lib/utils"
import { Card, CardContent } from "@/registry/new-york/ui/card"
import { Badge } from "@/registry/new-york/ui/badge"
import { Skeleton } from "@/registry/new-york/ui/skeleton"
import { AspectRatio } from "@/registry/new-york/ui/aspect-ratio"
import { useNFTGallery } from "./hooks/use-nft-gallery"
import type { NFT, NFTGalleryProps } from "./lib/types"
import { AlertCircle, ImageIcon } from "lucide-react"
import { Alert, AlertDescription } from "@/registry/new-york/ui/alert"

function NFTCard({ 
  nft, 
  showCollectionName = true, 
  showFloorPrice = false 
}: { 
  nft: NFT
  showCollectionName?: boolean
  showFloorPrice?: boolean
}) {
  const imageUrl = nft.media?.imageUrl?.medium || nft.media?.imageUrl?.large || nft.imageUrl
  const hasImage = !!imageUrl
  const collectionName = nft.collection?.name || nft.contract?.name
  const floorPrice = nft.floorAsk?.price?.amount?.usd || nft.collection?.floorAskPrice?.amount?.usd

  return (
    <Card className="group overflow-hidden transition-all hover:shadow-md">
      <div className="relative">
        <AspectRatio ratio={1} className="bg-muted">
          {hasImage ? (
            <img
              src={imageUrl}
              alt={nft.name || "NFT"}
              className="object-cover w-full h-full transition-transform group-hover:scale-105"
              loading="lazy"
              onError={(e) => {
                const target = e.target as HTMLImageElement
                target.style.display = "none"
                const fallback = target.nextElementSibling as HTMLElement
                if (fallback) fallback.style.display = "flex"
              }}
            />
          ) : null}
          <div 
            className={cn(
              "absolute inset-0 flex items-center justify-center bg-muted",
              hasImage && "hidden"
            )}
          >
            <ImageIcon className="h-8 w-8 text-muted-foreground" />
          </div>
        </AspectRatio>

        {nft.balance && parseInt(nft.balance) > 1 && (
          <Badge 
            variant="secondary" 
            className="absolute top-2 right-2 text-xs"
          >
            Ã—{nft.balance}
          </Badge>
        )}
      </div>

      <CardContent className="p-3">
        <div className="space-y-1">
          <h3 className="font-medium text-sm truncate" title={nft.name}>
            {nft.name || `#${nft.tokenId}`}
          </h3>

          {showCollectionName && collectionName && (
            <p className="text-xs text-muted-foreground truncate" title={collectionName}>
              {collectionName}
            </p>
          )}

          {showFloorPrice && floorPrice && (
            <p className="text-xs font-medium text-green-600">
              ${floorPrice.toFixed(2)}
            </p>
          )}
        </div>
      </CardContent>
    </Card>
  )
}

function NFTGallerySkeleton({ 
  columns = { default: 2, sm: 3, md: 4, lg: 6 },
  count = 12 
}: {
  columns?: NFTGalleryProps["columns"]
  count?: number
}) {
  const gridCols = {
    [`grid-cols-${columns?.default || 2}`]: true,
    [`sm:grid-cols-${columns?.sm || 3}`]: true,
    [`md:grid-cols-${columns?.md || 4}`]: true,
    [`lg:grid-cols-${columns?.lg || 6}`]: true,
  }

  return (
    <div className={cn("grid gap-4", gridCols)}>
      {Array.from({ length: count }).map((_, i) => (
        <Card key={i} className="overflow-hidden">
          <AspectRatio ratio={1}>
            <Skeleton className="h-full w-full" />
          </AspectRatio>
          <CardContent className="p-3">
            <div className="space-y-2">
              <Skeleton className="h-4 w-3/4" />
              <Skeleton className="h-3 w-1/2" />
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  )
}

/**
 * NFT Gallery - A responsive gallery component that displays a user's NFTs
 * 
 * Fetches and displays NFTs from the Abstract backend API in a responsive grid layout
 * with support for loading states, error handling, and customizable display options.
 */
export function NFTGallery({
  address,
  className,
  columns = { default: 2, sm: 3, md: 4, lg: 6 },
  showCollectionName = true,
  showFloorPrice = false,
  maxItems,
}: NFTGalleryProps) {
  const { data, isLoading, error } = useNFTGallery(address)

  const nfts = maxItems 
    ? data?.nfts?.slice(0, maxItems) || []
    : data?.nfts || []

  const gridCols = {
    [`grid-cols-${columns.default}`]: true,
    [`sm:grid-cols-${columns.sm}`]: true,
    [`md:grid-cols-${columns.md}`]: true,
    [`lg:grid-cols-${columns.lg}`]: true,
  }

  if (isLoading) {
    return (
      <div className={className}>
        <NFTGallerySkeleton columns={columns} />
      </div>
    )
  }

  if (error) {
    return (
      <div className={className}>
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            Failed to load NFTs. Please try again later.
          </AlertDescription>
        </Alert>
      </div>
    )
  }

  if (!nfts.length) {
    return (
      <div className={cn("text-center py-8", className)}>
        <ImageIcon className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
        <h3 className="text-lg font-medium mb-2">No NFTs found</h3>
        <p className="text-muted-foreground">
          This wallet doesn't have any NFTs yet.
        </p>
      </div>
    )
  }

  return (
    <div className={cn("grid gap-4", gridCols, className)}>
      {nfts.map((nft) => (
        <NFTCard
          key={nft.id}
          nft={nft}
          showCollectionName={showCollectionName}
          showFloorPrice={showFloorPrice}
        />
      ))}
    </div>
  )
}

import { useQuery } from "@tanstack/react-query"
import type { NFTGalleryResponse } from "../lib/types"

async function fetchUserNFTs(address: string): Promise<NFTGalleryResponse> {
  if (!address) {
    throw new Error("Address is required")
  }

  const response = await fetch(
    `https://backend.portal.abs.xyz/api/user/${address}/wallet/v2/nfts`
  )

  if (!response.ok) {
    throw new Error(`Failed to fetch NFTs: ${response.statusText}`)
  }

  return response.json()
}

export function useNFTs(address: string, enabled = true) {
  return useQuery({
    queryKey: ["nfts", address],
    queryFn: () => fetchUserNFTs(address),
    enabled: enabled && !!address,
    staleTime: 5 * 60 * 1000, // 5 minutes
    gcTime: 10 * 60 * 1000, // 10 minutes
    retry: 3,
    retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000),
  })
}

export interface NFTAttribute {
  trait_type: string
  value: string | number
}

export interface NFTContract {
  address: string
  name: string
  symbol: string
  type: "ERC721" | "ERC1155"
  openSeaSlug?: string
  description?: string
  imageUrl?: string
  externalUrl?: string
}

export interface NFTCollection {
  id: string
  name: string
  description?: string
  imageUrl?: string
  floorAskPrice?: {
    amount: {
      decimal: number
      usd: number
    }
    currency: {
      contract: string
      name: string
      symbol: string
      decimals: number
    }
  }
}

export interface NFTMedia {
  small?: string
  medium?: string
  large?: string
}

export interface NFTLastSale {
  orderSource: string
  fillSource?: string
  timestamp: number
  price?: {
    amount: {
      decimal: number
      usd: number
    }
    currency: {
      contract: string
      name: string
      symbol: string
      decimals: number
    }
  }
}

export interface NFT {
  id: string
  chain: string
  kind: string
  contract: NFTContract
  tokenId: string
  name: string
  description?: string
  imageUrl?: string
  imageOriginalUrl?: string
  animationOriginalUrl?: string
  metadataOriginalUrl?: string
  media?: {
    imageUrl?: NFTMedia
    videoUrl?: string
    audioUrl?: string
    modelUrl?: string
  }
  attributes?: NFTAttribute[]
  balance: string
  acquiredAt?: string
  collection: NFTCollection
  lastSale?: NFTLastSale
  floorAsk?: {
    price?: {
      amount: {
        decimal: number
        usd: number
      }
      currency: {
        contract: string
        name: string
        symbol: string
        decimals: number
      }
    }
  }
}

export interface NFTGalleryResponse {
  nfts: NFT[]
}

export interface NFTGalleryProps {
  address: string
  className?: string
  columns?: {
    default: number
    sm: number
    md: number
    lg: number
  }
  showCollectionName?: boolean
  showFloorPrice?: boolean
  maxItems?: number
}
