"use client"

import { useState, useEffect } from "react"
import { useAccount } from "wagmi"
import { cn } from "@/lib/utils"
import { type ClassValue } from "clsx"
import {
  Dialog,
  DialogContent,
  DialogTitle,
} from "@/registry/new-york/ui/dialog"
import { ConnectWalletButton } from "@/registry/new-york/blocks/connect-wallet-button/connect-wallet-button"
import { SiweButton } from "@/registry/new-york/blocks/siwe-button/siwe-button"
import { SessionKeyButton } from "@/registry/new-york/blocks/session-keys/session-key-button"
import { useSiweAuthQuery } from "@/registry/new-york/blocks/siwe-button/hooks/use-siwe-auth-query"
import { useSessionKey } from "@/registry/new-york/blocks/session-keys/hooks/use-session-key"

interface OnboardingDialogProps {
  steps?: {
    connectWallet?: boolean
    signWithEthereum?: boolean
    createSessionKey?: boolean
  }
  open?: boolean
  onOpenChange?: (open: boolean) => void
  onComplete?: () => void
  onStepComplete?: (step: string) => void
  className?: ClassValue
}

interface StepDefinition {
  id: string
  title: string
  subtitle: string
  component: React.ComponentType<{ className?: ClassValue }>
  validate: () => boolean
}

/**
 * Onboarding Dialog
 * 
 * A configurable multi-step onboarding dialog that guides users through:
 * - Wallet connection via ConnectWalletButton
 * - SIWE authentication via SiweButton
 * - Session key creation via SessionKeyButton
 * 
 * Features:
 * - Visual progress indicator showing current step
 * - Auto-detection of completed steps
 * - Configurable step selection
 * - Automatic advancement when steps complete
 */
export function OnboardingDialog({
  steps = {
    connectWallet: true,
    signWithEthereum: true,
    createSessionKey: true,
  },
  open = false,
  onOpenChange = () => {},
  onComplete = () => {},
  onStepComplete,
  className,
}: OnboardingDialogProps) {
  const { isConnected } = useAccount()
  const { data: authData } = useSiweAuthQuery()
  const { data: sessionData } = useSessionKey()

  const [currentStepIndex, setCurrentStepIndex] = useState(0)

  // Check authentication and session status
  const isAuthenticated = authData?.ok && authData?.user?.isAuthenticated
  const hasActiveSession = !!sessionData

  // Define all possible steps with their validation logic
  const allStepDefinitions: StepDefinition[] = [
    {
      id: 'connectWallet',
      title: 'Connect Wallet',
      subtitle: 'Connect your wallet to get started',
      component: ConnectWalletButton,
      validate: () => isConnected,
    },
    {
      id: 'signWithEthereum',
      title: 'Sign In with Ethereum',
      subtitle: 'Sign a message to authenticate your identity',
      component: SiweButton,
      validate: () => !!(isConnected && isAuthenticated),
    },
    {
      id: 'createSessionKey',
      title: 'Create Session Key',
      subtitle: 'Create a session key for seamless transactions',
      component: SessionKeyButton,
      validate: () => !!(isConnected && hasActiveSession),
    },
  ]

  // Filter to only enabled steps
  const enabledSteps = allStepDefinitions.filter(step => steps[step.id as keyof typeof steps])

  // Find the first incomplete step or stay at current
  const findCurrentStep = () => {
    for (let i = 0; i < enabledSteps.length; i++) {
      if (!enabledSteps[i].validate()) {
        return i
      }
    }
    return enabledSteps.length - 1 // All complete, stay at last step
  }

  // Update current step when validation changes
  useEffect(() => {
    if (open) {
      const newStepIndex = findCurrentStep()
      if (newStepIndex !== currentStepIndex) {
        setCurrentStepIndex(newStepIndex)
      }
    }
  }, [isConnected, isAuthenticated, hasActiveSession, open])

  // Check if all required steps are complete
  const allStepsComplete = enabledSteps.every(step => step.validate())

  // Handle completion
  useEffect(() => {
    if (allStepsComplete && open && enabledSteps.length > 0) {
      // Small delay to show completion state
      const timer = setTimeout(() => {
        onComplete()
        onOpenChange(false)
      }, 500)
      return () => clearTimeout(timer)
    }
  }, [allStepsComplete, open, onComplete, onOpenChange, enabledSteps.length])

  // Handle step completion callback
  useEffect(() => {
    if (onStepComplete && enabledSteps[currentStepIndex]?.validate()) {
      onStepComplete(enabledSteps[currentStepIndex].id)
    }
  }, [currentStepIndex, onStepComplete, enabledSteps])


  // Don't render if no steps are enabled
  if (enabledSteps.length === 0) {
    return null
  }

  const currentStep = enabledSteps[currentStepIndex]
  const StepComponent = currentStep?.component

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className={cn("sm:max-w-md", className)} showCloseButton={true}>
        <div className="flex flex-col items-center space-y-6">
          {/* Progress Dots */}
          <div className="flex justify-center space-x-2">
            {enabledSteps.map((step, index) => (
              <div
                key={step.id}
                className={cn(
                  "h-2 w-2 rounded-full transition-colors",
                  step.validate()
                    ? "bg-green-500"
                    : index === currentStepIndex
                      ? "bg-primary"
                      : "bg-muted"
                )}
              />
            ))}
          </div>

          {/* Step Title and Description */}
          <div className="text-center space-y-2">
            <DialogTitle className="text-xl font-semibold">{currentStep?.title}</DialogTitle>
            <p className="text-muted-foreground">{currentStep?.subtitle}</p>
          </div>

          {/* Current Step Component */}
          <div className="w-full">
            {StepComponent && (
              <StepComponent className="w-full" />
            )}
          </div>

        </div>
      </DialogContent>
    </Dialog>
  )
}

"use client";

import { useCallback, useMemo } from "react";
import { useAccount } from "wagmi";
import { useSiweAuthQuery } from "@/registry/new-york/blocks/siwe-button/hooks/use-siwe-auth-query";
import { useSessionKey } from "@/registry/new-york/blocks/session-keys/hooks/use-session-key";
import { requireOnboarding } from "../lib/require-onboarding";

interface OnboardingSteps {
  connectWallet?: boolean;
  signWithEthereum?: boolean;
  createSessionKey?: boolean;
}

interface UseOnboardingReturn {
  ready: boolean;
  require: () => boolean;
  showDialog: () => void;
  isLoading: boolean;
  isError: boolean;
  error?: Error;
}

/**
 * Hook for managing onboarding requirements and gating functionality
 *
 * @param steps Configuration for required onboarding steps
 * @returns Object with ready state, loading/error states, and control functions
 * @returns ready - Whether all required onboarding steps are completed
 * @returns isLoading - Whether we're loading data needed to determine onboarding state
 * @returns isError - Whether any authentication queries have errors
 * @returns error - The first error encountered, if any
 * @returns require - Function to gate actions behind onboarding completion
 * @returns showDialog - Function to manually show the onboarding dialog
 *
 * @example
 * ```tsx
 * const { ready, isLoading, isError, error, require } = useOnboarding({
 *   connectWallet: true,
 *   signWithEthereum: true,
 *   createSessionKey: false
 * })
 *
 * // Show loading state while determining onboarding status
 * if (isLoading) {
 *   return <div>Loading...</div>
 * }
 *
 * // Show error state if authentication fails
 * if (isError) {
 *   return <div>Error: {error?.message}</div>
 * }
 *
 * const handleClick = () => {
 *   if (!require()) return // Shows dialog if not ready
 *   doProtectedAction() // Only runs if onboarded
 * }
 * ```
 */
export function useOnboarding(steps: OnboardingSteps): UseOnboardingReturn {
  // Get current authentication state
  const { isConnected, isConnecting, isReconnecting } = useAccount();
  const authQuery = useSiweAuthQuery();
  const sessionQuery = useSessionKey();

  const { data: authData } = authQuery;
  const { data: sessionData } = sessionQuery;

  // Check authentication and session status
  const isAuthenticated = !!(authData?.ok && authData?.user?.isAuthenticated);
  const hasActiveSession = !!sessionData;

  // Show loading while we don't have enough data to determine the user's onboarding state
  // Treat required steps with unknown state (no data and no error) as loading, but only once wallet is connected
  const requireConnect = !!steps.connectWallet;
  const requireAuth = !!steps.signWithEthereum;
  const requireSession = !!steps.createSessionKey;

  // Only consider auth/session loading once a wallet is connected
  const missingAuthState =
    requireAuth &&
    isConnected &&
    typeof authQuery.data === "undefined" &&
    !authQuery.error;
  const missingSessionState =
    requireSession &&
    isConnected &&
    typeof sessionQuery.data === "undefined" &&
    !sessionQuery.error;

  // If connectWallet is required and we are not connected, don't block on loading unless we are actively connecting/reconnecting
  const isLoading = !!(
    isConnecting ||
    isReconnecting ||
    (!isConnected && requireConnect
      ? false
      : missingAuthState || missingSessionState)
  );

  // Debug logging
  console.log("ðŸ” useOnboarding Debug:", {
    isConnected,
    isConnecting,
    isReconnecting,
    "authQuery.isLoading": authQuery.isLoading,
    "authQuery.data": !!authQuery.data,
    "authQuery.error": !!authQuery.error,
    "sessionQuery.isLoading": sessionQuery.isLoading,
    "sessionQuery.data": !!sessionQuery.data,
    "sessionQuery.error": !!sessionQuery.error,
    isLoading,
  });

  // Aggregate error states
  const isError = useMemo(() => {
    return !!(authQuery.error || sessionQuery.error);
  }, [authQuery.error, sessionQuery.error]);

  // Get first error encountered for debugging
  const error = useMemo<Error | undefined>(() => {
    return authQuery.error || sessionQuery.error || undefined;
  }, [authQuery.error, sessionQuery.error]);

  // Check if all required steps are completed
  const ready = useMemo(() => {
    const stepStates = {
      connectWallet: isConnected,
      signWithEthereum: isConnected && isAuthenticated,
      createSessionKey: isConnected && hasActiveSession,
    };

    return Object.entries(steps).every(([stepKey, required]) => {
      if (!required) return true;
      return stepStates[stepKey as keyof typeof stepStates];
    });
  }, [steps, isConnected, isAuthenticated, hasActiveSession]);

  // Function to gate actions behind onboarding completion
  const require = useCallback((): boolean => {
    if (ready) {
      // All required steps completed
      return true;
    } else {
      // Missing required steps, show the onboarding modal using existing global function
      requireOnboarding(steps, () => {
        // This will be called when onboarding completes, but we don't need to do anything
        // since the user's action will be in their own code after the require() call
      });
      return false;
    }
  }, [ready, steps]);

  // Function to manually show the dialog
  const showDialog = useCallback(() => {
    requireOnboarding(steps, () => {
      // Manual dialog show, no action needed on completion
    });
  }, [steps]);

  // Final debug log before return
  console.log("ðŸ” Final return values:", { ready, isLoading, isError });

  return {
    ready,
    require,
    showDialog,
    isLoading,
    isError,
    error,
  };
}

import { useOnboardingContext } from "./onboarding-context"

interface OnboardingSteps {
  connectWallet?: boolean
  signWithEthereum?: boolean
  createSessionKey?: boolean
}

// Global reference to the onboarding context
let globalRequireOnboarding: ((
  steps: OnboardingSteps,
  callback: () => void,
  onComplete?: () => void
) => void) | null = null

// Hook to register the global function (used by Onboarder component)
export function useRegisterOnboarding() {
  const { requireOnboarding } = useOnboardingContext()

  // Register the global function
  if (typeof window !== "undefined") {
    globalRequireOnboarding = requireOnboarding
  }

  return requireOnboarding
}

/**
 * Global function to require onboarding (like toast() from sonner)
 * 
 * @param steps - Which onboarding steps to require
 * @param callback - Function to execute when onboarding is complete
 * @param onComplete - Optional callback when onboarding dialog closes
 */
export function requireOnboarding(
  steps: OnboardingSteps,
  callback: () => void,
  onComplete?: () => void
) {
  if (!globalRequireOnboarding) {
    console.error(
      "requireOnboarding called before <Onboarder /> was mounted. " +
      "Make sure to add <Onboarder /> to your app root."
    )
    return
  }

  globalRequireOnboarding(steps, callback, onComplete)
}

// Convenience functions for common patterns
export const onboarding = {
  /**
   * Require wallet connection only
   */
  connectWallet: (callback: () => void, onComplete?: () => void) =>
    requireOnboarding({ connectWallet: true }, callback, onComplete),

  /**
   * Require wallet connection + SIWE auth
   */
  authenticate: (callback: () => void, onComplete?: () => void) =>
    requireOnboarding(
      { connectWallet: true, signWithEthereum: true },
      callback,
      onComplete
    ),

  /**
   * Require full onboarding (all steps)
   */
  full: (callback: () => void, onComplete?: () => void) =>
    requireOnboarding(
      {
        connectWallet: true,
        signWithEthereum: true,
        createSessionKey: true,
      },
      callback,
      onComplete
    ),
}

"use client"

import React, { createContext, useContext, useState, useCallback } from "react"
import { useAccount } from "wagmi"
import { useSiweAuthQuery } from "@/registry/new-york/blocks/siwe-button/hooks/use-siwe-auth-query"
import { useSessionKey } from "@/registry/new-york/blocks/session-keys/hooks/use-session-key"

interface OnboardingSteps {
  connectWallet?: boolean
  signWithEthereum?: boolean
  createSessionKey?: boolean
}

interface OnboardingRequest {
  id: string
  steps: OnboardingSteps
  callback: () => void
  onComplete?: () => void
}

interface OnboardingContextType {
  // Internal state
  currentRequest: OnboardingRequest | null
  isOpen: boolean

  // Public API
  requireOnboarding: (steps: OnboardingSteps, callback: () => void, onComplete?: () => void) => void

  // Internal methods for the Onboarder component
  _handleComplete: () => void
  _handleClose: () => void
  _isReady: (steps: OnboardingSteps) => boolean
}

const OnboardingContext = createContext<OnboardingContextType | null>(null)

interface OnboardingProviderProps {
  children: React.ReactNode
}

export function OnboardingProvider({ children }: OnboardingProviderProps) {
  const [currentRequest, setCurrentRequest] = useState<OnboardingRequest | null>(null)
  const [isOpen, setIsOpen] = useState(false)

  // Get current authentication state
  const { isConnected } = useAccount()
  const { data: authData } = useSiweAuthQuery()
  const { data: sessionData } = useSessionKey()

  // Check authentication and session status
  const isAuthenticated = !!(authData?.ok && authData?.user?.isAuthenticated)
  const hasActiveSession = !!sessionData

  // Check if required steps are completed
  const _isReady = useCallback((steps: OnboardingSteps): boolean => {
    const stepStates = {
      connectWallet: isConnected,
      signWithEthereum: isConnected && isAuthenticated,
      createSessionKey: isConnected && hasActiveSession,
    }

    return Object.entries(steps).every(([stepKey, required]) => {
      if (!required) return true
      return stepStates[stepKey as keyof typeof stepStates]
    })
  }, [isConnected, isAuthenticated, hasActiveSession])

  const requireOnboarding = useCallback((
    steps: OnboardingSteps,
    callback: () => void,
    onComplete?: () => void
  ) => {
    if (_isReady(steps)) {
      // All required steps completed, execute immediately
      callback()
    } else {
      // Missing required steps, show onboarding modal
      const request: OnboardingRequest = {
        id: Math.random().toString(36).substr(2, 9),
        steps,
        callback,
        onComplete,
      }
      setCurrentRequest(request)
      setIsOpen(true)
    }
  }, [_isReady])

  const _handleComplete = useCallback(() => {
    if (currentRequest) {
      // Execute the original callback
      currentRequest.callback()

      // Call the completion callback if provided
      currentRequest.onComplete?.()

      // Clean up
      setCurrentRequest(null)
      setIsOpen(false)
    }
  }, [currentRequest])

  const _handleClose = useCallback(() => {
    setIsOpen(false)
    setCurrentRequest(null)
  }, [])

  return (
    <OnboardingContext.Provider
      value={{
        currentRequest,
        isOpen,
        requireOnboarding,
        _handleComplete,
        _handleClose,
        _isReady,
      }}
    >
      {children}
    </OnboardingContext.Provider>
  )
}

export function useOnboardingContext() {
  const context = useContext(OnboardingContext)
  if (!context) {
    throw new Error("useOnboardingContext must be used within OnboardingProvider")
  }
  return context
}

"use client";

import { useEffect } from "react";
import {
  OnboardingProvider,
  useOnboardingContext,
} from "@/registry/new-york/blocks/onboarding-dialog/lib/onboarding-context";
import { useRegisterOnboarding } from "@/registry/new-york/blocks/onboarding-dialog/lib/require-onboarding";
import { OnboardingDialog } from "@/registry/new-york/blocks/onboarding-dialog/onboarding-dialog";

function OnboarderInner() {
  const { currentRequest, isOpen, _handleComplete, _handleClose, _isReady } =
    useOnboardingContext();

  // Register the global requireOnboarding function
  useRegisterOnboarding();

  // Auto-complete if steps become ready while dialog is open
  useEffect(() => {
    if (currentRequest && isOpen && _isReady(currentRequest.steps)) {
      // Small delay to show completion state
      const timer = setTimeout(() => {
        _handleComplete();
      }, 500);
      return () => clearTimeout(timer);
    }
  }, [currentRequest, isOpen, _isReady, _handleComplete]);

  // Don't render anything if no active request
  if (!currentRequest) {
    return null;
  }

  return (
    <OnboardingDialog
      steps={currentRequest.steps}
      open={isOpen}
      onOpenChange={(open) => {
        if (!open) {
          _handleClose();
        }
      }}
      onComplete={_handleComplete}
    />
  );
}

/**
 * Onboarder Component
 *
 * Similar to Sonner's <Toaster />, this component should be placed once at your app root.
 * It renders onboarding dialogs when requireOnboarding() is called anywhere in your app.
 *
 * Usage:
 * ```tsx
 * // In your app root (layout.tsx or _app.tsx)
 * import { Onboarder } from "@/components/onboarder"
 *
 * export default function RootLayout({ children }) {
 *   return (
 *     <html>
 *       <body>
 *         {children}
 *         <Onboarder />
 *       </body>
 *     </html>
 *   )
 * }
 *
 * // Anywhere in your app
 * import { requireOnboarding } from "@/lib/require-onboarding"
 *
 * function MyComponent() {
 *   const handleAction = () => {
 *     requireOnboarding(
 *       { connectWallet: true, signWithEthereum: true },
 *       () => console.log("Action executed!")
 *     )
 *   }
 * }
 * ```
 */
export function Onboarder() {
  return (
    <OnboardingProvider>
      <OnboarderInner />
    </OnboardingProvider>
  );
}

"use client"

import { useLoginWithAbstract } from "@abstract-foundation/agw-react"
import { Button } from "@/registry/new-york/ui/button"
import { useAccount, useBalance } from "wagmi"
import { cn } from "@/lib/utils"
import { type ClassValue } from "clsx"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/registry/new-york/ui/dropdown-menu"
import { useState } from "react"

interface ConnectWalletButtonProps {
  className?: ClassValue
  customDropdownItems?: React.ReactNode[]
}

/**
 * Connect Wallet Button for Abstract Global Wallet
 * 
 * A comprehensive wallet connection component that handles:
 * - Wallet connection/disconnection via Abstract Global Wallet
 * - Loading states during connection
 * - Balance display with wallet and Abstract logos
 * - Dropdown menu with address copy functionality
 */
export function ConnectWalletButton({ className, customDropdownItems }: ConnectWalletButtonProps) {
  // Wagmi hooks for wallet state and balance
  const { isConnected, status, address } = useAccount()
  const { data: balance, isLoading: isBalanceLoading } = useBalance({ address })

  // Abstract Global Wallet authentication
  const { login, logout } = useLoginWithAbstract()

  // Local state for connection status and copy feedback
  const isConnecting = status === 'connecting' || status === 'reconnecting'
  const [copied, setCopied] = useState(false)

  /**
   * Copy wallet address to clipboard with visual feedback
   */
  const copyAddress = async () => {
    if (address) {
      await navigator.clipboard.writeText(address)
      setCopied(true)
      setTimeout(() => setCopied(false), 2000)
    }
  }

  // Loading state: Show connecting button with spinning logo
  if (isConnecting) {
    return (
      <Button
        disabled
        className={cn("cursor-pointer group min-w-40", className)}
      >
        Connecting...
        <AbstractLogo className="ml-2 animate-spin" />
      </Button>
    )
  }

  // Disconnected state: Show connect button with hover animation
  if (!isConnected) {
    return (
      <Button
        onClick={login}
        className={cn("cursor-pointer group min-w-40", className)}
      >
        Connect Wallet
        <AbstractLogo className="ml-2 group-hover:animate-spin transition-transform" />
      </Button>
    )
  }

  // Connected but loading balance: Show loading state
  if (isConnected && isBalanceLoading) {
    return (
      <Button
        disabled
        className={cn("cursor-pointer group min-w-40 px-3", className)}
      >
        <WalletIcon className="mr-1 h-4 w-4" />
        Loading...
        <AbstractLogo className="ml-1 h-4 w-4 animate-spin" />
      </Button>
    )
  }

  // Format the balance for display (4 decimal places)
  const formattedBalance = balance
    ? `${parseFloat(balance.formatted).toFixed(4)} ${balance.symbol}`
    : '0.0000 ETH'

  // Connected state: Show balance with dropdown menu
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          className={cn("cursor-pointer group min-w-40 px-3", className)}
        >
          <WalletIcon className="mr-1 h-4 w-4" />
          {formattedBalance}
          <AbstractLogo className="ml-1 h-4 w-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="center" side="bottom" className="w-56">
        {/* Address display with copy functionality */}
        <DropdownMenuItem className="focus:bg-transparent cursor-auto">
          <div className="flex items-center justify-between w-full">
            <span className="text-xs text-muted-foreground font-mono">
              {address ? `${address.slice(0, 6)}...${address.slice(-4)}` : 'Connected'}
            </span>
            {address && (
              <Button
                variant="ghost"
                size="sm"
                className="h-6 w-6 p-0 hover:bg-accent"
                onClick={(e) => {
                  // Prevent dropdown from closing when copying
                  e.preventDefault()
                  e.stopPropagation()
                  copyAddress()
                }}
              >
                {copied ? (
                  <CheckIcon className="h-2.5 w-2.5" />
                ) : (
                  <CopyIcon className="h-2.5 w-2.5" />
                )}
              </Button>
            )}
          </div>
        </DropdownMenuItem>
        {/* Custom dropdown items or default disconnect */}
        {customDropdownItems ? (
          customDropdownItems
        ) : (
          <>
            <DropdownMenuSeparator />
            <DropdownMenuItem onClick={logout} className="text-destructive">
              Disconnect
            </DropdownMenuItem>
          </>
        )}
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

function WalletIcon({ className }: { className?: ClassValue }) {
  return (
    <svg
      width="16"
      height="16"
      viewBox="0 0 640 640"
      xmlns="http://www.w3.org/2000/svg"
      className={cn(className)}
    >
      <path
        d="M128 96C92.7 96 64 124.7 64 160L64 448C64 483.3 92.7 512 128 512L512 512C547.3 512 576 483.3 576 448L576 256C576 220.7 547.3 192 512 192L136 192C122.7 192 112 181.3 112 168C112 154.7 122.7 144 136 144L520 144C533.3 144 544 133.3 544 120C544 106.7 533.3 96 520 96L128 96zM480 320C497.7 320 512 334.3 512 352C512 369.7 497.7 384 480 384C462.3 384 448 369.7 448 352C448 334.3 462.3 320 480 320z"
        fill="currentColor"
      />
    </svg>
  )
}
function CopyIcon({ className }: { className?: ClassValue }) {
  return (
    <svg
      width="16"
      height="16"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
      className={cn(className)}
    >
      <rect width="14" height="14" x="8" y="8" rx="2" ry="2" />
      <path d="M4 16c-1.1 0-2-.9-2-2V4c0-1.1.9-2 2-2h10c1.1 0 2 .9 2 2" />
    </svg>
  )
}

function CheckIcon({ className }: { className?: ClassValue }) {
  return (
    <svg
      width="16"
      height="16"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
      className={cn(className)}
    >
      <path d="M20 6L9 17l-5-5" />
    </svg>
  )
}

function AbstractLogo({ className }: { className?: ClassValue }) {
  return (
    <svg
      width="20"
      height="18"
